-- convert all text strings in table to lowercase
lowertable = function(t)
	for i=0,#t do
		if type(t[i]) == "table" then
			lowertable(t[i])
		elseif type(t[i]) == "string" then
			t[i] = t[i]:lower()
		end
	end
end
lowertable(t)

-- metatable for the CraftInfo tables that are sorted by skill
local cmt = {}
cmt.__index = function(t,k)
	k = k:lower()
	for i=1,#t do
		if k == t[i][1]:lower() then
			return t[i]
		end
	end
end

-- items sorted by skillname (also using cmt metatable)
for i=1,#t do
	local Skill = t[i][0]
	if t[Skill] == nil then
		t[Skill] = {}
		setmetatable(t[Skill],cmt)
	end
	table.insert(t[Skill],t[i])
end

setmetatable(t,cmt)

-- metatable for each item in list
local imt = {}
imt.__index = function(t,k)
	if type(k) ~= "string" then
		return
	end
	k = k:sub(1,3):lower()
	if k == "nam" then return t[1]
	elseif k == "cat" then return t[2]
	elseif k == "sel" then return t[3]
	elseif k == "ski" then return t[4]
	elseif k == "mat" then return t[5]
	elseif k == "oth" then return t[6]
	elseif k == "typ" then return t[7] end
end

-- metatable for each items skill table
local smt = {}
smt.__index = function(t,k)
	if type(k) ~= "string" then
		return rawget(t,k)
	end
	k = k:sub(1,3):lower()
	if k == "nam" then return t[1]
	elseif k == "val" then return t[2] end
end

-- metatable for each items material table
local mmt = {}
mmt.__index = function(t,k)
	if type(k) ~= "string" then
		return rawget(t,k)
	end
	k = k:lower():sub(1,3)
	if k == "nam" then return t[1]
	elseif k == "val" then return t[2] end
end

-- set all items item, skill and material metatables
for i=1,#t do
	setmetatable(t[i],imt)
	for j=1,#t[i][4] do
		setmetatable(t[i][4][j],smt)
	end
	for j=1,#t[i][5] do
		setmetatable(t[i][5][j],mmt)
	end
end

-- compatibility with "old" style CraftInfo
for i=1,#t do
	local skill = t[i][0]
	if t[skill] == nil then
		t[skill] = {}
	end
	t[skill][t[i].nam] = {
			category = t[i][2],
			selection = t[i][3],
			skill = t[i][4],
			material = t[i][5],
			other = t[i][6],
			type = t[i][7]}
end

return t
